class Ajax{post(url,data,callback){const request=new XMLHttpRequest();request.open('POST',url,true);request.responseType='json';request.setRequestHeader('Content-Type','Application/JSON');request.onload=()=>{callback(null,request.response);};request.onerror=()=>{console.log(request.status,':',request.statusText);callback(true,request.status);};request.send(JSON.stringify(data));}get(url,callback){const request=new XMLHttpRequest();request.open('GET',url,true);request.responseType='json';request.send();request.onload=()=>{callback(false,request.response);};request.onerror=()=>{console.log(request.status,':',request.statusText);callback(true,request.status);};}}
